function wearable_check_if_legit()  local LEGIT_CHECK = true--  if getServerName() ~= "|| Multi Theft Auto Roleplay | www.mtarp.co ||" then--    outputDebugString("[WEARABLE-SECURITY]: server name mismatch! :O")--    LEGIT_CHECK = false--  end  if getResourceName(getThisResource()) ~= "wearable-system" then    outputDebugString("[WEARABLE-SECURITY]: resource name mismatch! :O")    LEGIT_CHECK = false  end--  if getMaxPlayers() ~= 40 then--    outputDebugString("[WEARABLE-SECURITY]: player slot mismatch! :O")--    LEGIT_CHECK = false--  end--  if not LEGIT_CHECK then--    outputDebugString("[WEARABLE-SECURITY]: one of the checks did not pass the security check; sending fail signal.")--  else--    outputDebugString("[WEARABLE-SECURITY]: all of the checks passed the security check; sending the success signal.")--  end  local SECURITY_TABLE = {    getServerName(),    getResourceName(getThisResource()),    getMaxPlayers()  }  triggerClientEvent(source, "wearable:security_check:recieve", source, SECURITY_TABLE)endaddEvent("wearable:security_check", true)addEventHandler("wearable:security_check", getRootElement(), wearable_check_if_legit)